mapper.select:
SELECT id, title, content, writer, reg_date AS regDate, like_cnt AS likeCnt, view_cnt AS viewCnt, comment_cnt AS commentCnt FROM community
${key} = #{word}
${key} LIKE concat('%', #{word}, '%')

mapper.insert:
INSERT INTO community
(title, writer, content, reg_date, like_cnt, view_cnt, comment_cnt)
VALUES (#{title}, #{writer}, #{content}, now(), 0, 0, 0);

mapper.update:
UPDATE community
SET title
= #{title}, content = #{content}
WHERE id = #{id};
