mapper.select:
SELECT id, name, nickname, email, age, fitness_level AS fitnessLevel, post_code AS postCode, address, liked_videos AS likedVideos, img
FROM users
WHERE id = #{id} AND password = #{password}

mapper.update:
UPDATE users
SET name = #{name}, nickname=#{nickname}, age=#{age}, fitness_level=#{fitnessLevel}, post_code=#{postCode}, address=#{address}
WHERE id = #{id}

UPDATE users
SET liked_videos=#{likedVideos}
WHERE id = #{id}

UPDATE users
SET img = #{imageUrl}
WHERE id = #{userId}
