mapper.select:
SELECT COUNT(*)
FROM users
WHERE id = #{id} AND password = #{password}

mapper.update:
UPDATE users
SET password=#{password}
WHERE id=#{id}

UPDATE users
SET nickname=#{nickname}, age=#{age},
fitness_level=#{fitnessLevel}, post_code=#{postCode},
address=#{address}
WHERE id = #{id}

UPDATE users
SET liked_videos=#{likedVideos}
WHERE id = #{id}

UPDATE users
SET liked_community=#{likedCommunity}	
WHERE id = #{id}

UPDATE users
SET
saved_routine=#{savedRoutine}
WHERE id = #{id}

UPDATE users
SET img = #{imageUrl}
WHERE id = #{userId}
